CREATE MATERIALIZED VIEW "public"."instructor_sections_m_view" AS (select "instructor"."id" as "instructor_id", "instructor"."name" as "instructor_name", "section"."crn" as "section_crn", "section"."term_id" as "term_id", "section"."subject_code" as "subject_code", "section"."course_number" as "course_number", "course"."title" as "course_title", "section"."section" as "section_code", "section"."instruction_method" as "instruction_method", "section"."instruction_type" as "instruction_type" from "instructor" inner join "instructor_sections" on "instructor_sections"."instructor_id" = "instructor"."id" inner join "section" on "section"."crn" = "instructor_sections"."section_crn" inner join "course" on "course"."id" = "section"."course_id" inner join "subject" on "subject"."id" = "section"."subject_code");